            for (let cookie of cookies) {
                cookie = cookie.trim();
                if (cookie === `${cookieName}=${cookieValue}`) {
                    document.querySelector('.cookies').classList.remove("cookies--show");
                    return true;
                }
            }

            document.querySelector('.cookies').classList.add("cookies--show");
        }
        // ---------- /cookies banner ---------- //*/
</script>

<?php wp_footer(); ?>

</body>
</html>
