    <section class="section news">
        <div class="container">

              <?php $args = array(
                'post_type'              => 'post',
                'post_status'            => 'publish',
                'pagination'             => true,
                'paged'                  => (get_query_var('paged') ? get_query_var('paged') : 1),
                'posts_per_page'         => 20,
                'order'                  => 'DSC',
                'orderby'                => 'date',
              );

                $wp_query = new WP_Query($args); ?>


            <?php if ( $wp_query->have_posts() ) : while ( $wp_query->have_posts() ) : $wp_query->the_post(); ?>

            <?php $row = (($wp_query->current_post+1) % 2) ?>

                <?php if ($row) { ?>
                <div class="row">
                <?php } ?>

                    <div class="col-md-6">
                        <article class="news__article">
                            <?php if ( has_post_thumbnail()) : ?>
                                <?php the_post_thumbnail(); ?>
                            <?php endif; ?>
                            <div class="news__content">
                                <h3 class="news__header"><?php the_title(); ?>
                                </h3>
                                <time class="news__date"><?php the_date(); ?></time>

                                <div class="news__text">
                                    <?php echo the_excerpt( wp_trim_words( get_the_content()), 10, '...' ); ?>
                                </div>

                                <a class="news__more" href="<?php the_permalink(); ?>" alt="<?php the_title_attribute(); ?>">Czytaj wiÄ™cej Â»</a>
                            </div>
                        </article>
                    </div>

                <?php if (!$row) { ?>
                </div>
                <?php } ?>

                <?php endwhile; endif; ?>

            </div>

            <?php get_template_part( 'nav', 'below' ); ?>
        </div>
    </section>

